INSERT INTO `vcd_ebook` (`id`, `name`, `description`, `event`, `image`, `ebook`, `position`, `visible`, `downloaded`) VALUES
(1,	'Nová čarodějka od Kříže',	'<p>Mal&aacute; čarodějka Božena, kter&aacute; se ale rozhodla, že si bude ř&iacute;kat Anabella, objevuje uměn&iacute; magie v&nbsp;humorn&eacute;m př&iacute;běhu z&nbsp;mal&eacute; vesničky, kde se uk&aacute;že, že i v&nbsp;modern&iacute; době se kouzla mohou st&aacute;t skutečnost&iacute;! Ale pozor! I když si Anabella s&nbsp;kouzly užije spoustu z&aacute;bavy, tak&eacute; se během sv&eacute;ho dobrodružstv&iacute; nauč&iacute;, že čarov&aacute;n&iacute; může b&yacute;t nebezpečn&eacute;, když se prov&aacute;d&iacute; neopatrně! Přečtěte si srandovn&iacute; př&iacute;běh pro kluky i holky, u kter&eacute;ho se pobav&iacute; i rodiče!</p>\n\n<p>Tuto poh&aacute;dku jsme pro děti napsali před na&scaron;&iacute;m m&aacute;jov&yacute;m v&yacute;letem ve Znojmě, kde jsme si užili i p&aacute;len&iacute; čarodějnic! U t&aacute;bor&aacute;ku, když plam&iacute;nek čarovně tančil v&nbsp;tajemn&eacute; tmě, jsme si po nočn&iacute; hře vypr&aacute;věli tento př&iacute;běh, abychom dětem uk&aacute;zali, že i ony stejně jako Anabella mohou zaž&iacute;t kouzeln&eacute; dobrodružstv&iacute;.</p>\n',	24,	'/ebooks/1/Knizka.png',	'/ebooks/1/Carodejka-od-krize-ebook.pdf',	5,	1,	212),
(2,	'Kdo sežral křečka?',	'<p>Zv&iacute;ř&aacute;tka v jednom dalek&eacute;m a hlubok&eacute;m lese se sejdou v medvědově chaloupce, usednou u stolu a mus&iacute; vyře&scaron;it velikou z&aacute;hadu. Kdo sežral křečka? Byl to snad s&aacute;m medvěd, nebo sova, no snad to nebyla laň? A co v tomhle lese pohled&aacute;v&aacute; lev? Čten&aacute;ři, nebojte se, nejedn&aacute; se o detektivn&iacute; z&aacute;hadu, kter&aacute; ned&aacute; dětem sp&aacute;t, ale naopak v&aacute;m přin&aacute;&scaron;&iacute;me poh&aacute;dkov&yacute; př&iacute;běh před span&iacute;m, kter&yacute; zaujme, pobav&iacute; a snad i trochu pouč&iacute;. Nic nen&iacute; takov&eacute;, jako se na prvn&iacute; pohled zd&aacute;, a zv&iacute;ř&aacute;tka v rol&iacute;ch detektivů v&aacute;s jistě překvap&iacute;.</p>\n\n<p>Tuto poh&aacute;dku jsme dětem četli na detektivn&iacute; v&iacute;kendovce v Boskovic&iacute;ch, kde jsme ře&scaron;ili i př&iacute;pad ukraden&yacute;ch diplomů, poh&aacute;ru a&nbsp;čokol&aacute;d. Zaj&iacute;m&aacute; v&aacute;s, jakpak je to s křečkem? Přečtěte si n&aacute;&scaron; př&iacute;běh.</p>\n',	23,	'/ebooks/2/kdosezralkrecka.png',	'/ebooks/2/Kdosezralkrecka.pdf',	4,	1,	329),
(3,	'Lov na vlkodlaka',	'<p>V&nbsp;poh&aacute;dkov&eacute;m př&iacute;běhu o lovu na vlkodlaka se vypr&aacute;v&iacute; o dobrodružstv&iacute; dvou neobyčejn&yacute;ch dět&iacute;, vycvičen&yacute;ch v&nbsp;magick&yacute;ch a &scaron;erm&iacute;řsk&yacute;ch dovednostech zakl&iacute;načů. Př&iacute;běh zakl&iacute;načů se odehr&aacute;l před d&aacute;vn&yacute;mi časy v&nbsp;poh&aacute;dkov&eacute; zemi, kde žili vlkodlaci, gh&uacute;lov&eacute; a lesn&iacute; skřeti, a tak&eacute; zakl&iacute;nači, kteř&iacute; měli za &uacute;kol tato monstra lovit a zbavovat zaklet&iacute;. Ale n&aacute;&scaron; dobrodružn&yacute; zakl&iacute;načsk&yacute; t&aacute;bor se odehr&aacute;l &uacute;plně ned&aacute;vno od 13. do 20. srpna 2016 ve Vranově nad Dyj&iacute;!</p>\n\n<p>Po cel&yacute; t&yacute;den jsme se cvičili v&nbsp;&scaron;ermu, vyr&aacute;běli si vlastn&iacute; ku&scaron;e, magick&eacute; amulety a učili se o d&aacute;vn&yacute;ch př&iacute;&scaron;er&aacute;ch, kter&eacute; mus&iacute;me porazit. Celou dobu n&aacute;s prov&aacute;zel př&iacute;běh o mal&yacute;ch zakl&iacute;nač&iacute;ch Gerenovi a Yeně, kteř&iacute; se sv&yacute;m mistrem vyr&aacute;žej&iacute; na svou prvn&iacute; v&yacute;pravu, aby mu pomohli ulovit vlkodlaka. A krom toho jsme si tak&eacute; n&aacute;ramně užili z&aacute;věr letn&iacute;ch pr&aacute;zdnin, nav&scaron;t&iacute;vili během her n&aacute;dhern&eacute; lesy v&nbsp;okol&iacute;, několikr&aacute;t tak&eacute; vranovskou pl&aacute;ž a m&iacute;stn&iacute; z&aacute;mek. Na z&aacute;žitky z&nbsp;na&scaron;eho fantastick&eacute;ho dobrodružstv&iacute; jistě nikdy nezapomeneme. Jako vzpom&iacute;nka n&aacute;m zůst&aacute;v&aacute; zakl&iacute;načsk&yacute; př&iacute;běh, kter&yacute; pro n&aacute;s vedouc&iacute; napsali, a kter&yacute; si teď můžete přeč&iacute;st i vy.</p>\n',	7,	'/ebooks/3/Obal3D.png',	'/ebooks/3/Lov-na-vlkodlaka.pdf',	3,	1,	187),
(4,	'Hry špionů',	'<p>Na &scaron;pionsk&eacute; z&aacute;kladně v Brně od 20. do 22. ledna jsme vycvičili novou skupinu &scaron;pionů. Měli za &uacute;kol nasb&iacute;rat co nejv&iacute;ce kartiček z origin&aacute;ln&iacute; &scaron;pionsk&eacute; hry, splnit v&scaron;echny akčn&iacute; &uacute;koly v zasněžen&eacute; krajině a zachr&aacute;nit svět před zmrznut&iacute;m. Jelikož se jim to v&scaron;e kr&aacute;sně podařilo, za odměnu nav&scaron;t&iacute;vili z&aacute;bavn&iacute; park Bongo. Př&iacute;běh, kter&yacute; si n&iacute;že přečtete, byl instrukt&aacute;žn&iacute;m manu&aacute;lem, jak se st&aacute;t nejlep&scaron;&iacute;m tajn&yacute;m agentem. Provedou v&aacute;s j&iacute;m dva zač&iacute;naj&iacute;c&iacute; mlad&iacute; agenti, kteř&iacute; dok&aacute;žou čelit zlovoln&eacute; teroristick&eacute; s&iacute;ti, kter&aacute; m&aacute; v&nbsp;pl&aacute;nu ovl&aacute;dnout svět. Akčn&iacute; př&iacute;běh volně inspirovan&yacute; Jamesem Bondem v&aacute;s uvede do děje, kde &scaron;lechetn&eacute; z&aacute;měry, z&aacute;bavn&eacute; modern&iacute; vyn&aacute;lezy a dětsk&aacute; hravost maj&iacute; svoje m&iacute;sto!</p>\n',	NULL,	'/ebooks/4/Hryspionu3D-1-.png',	'/ebooks/4/Hry-spionu.pdf',	2,	1,	177),
(5,	'Ztraceni v tajuplném lese',	'<p dir=\"ltr\">M&aacute;te r&aacute;di toulky kr&aacute;sn&yacute;mi česk&yacute;mi lesy? A zn&aacute;te, jak&eacute; je to se v&nbsp;lese ztratit? Prož&iacute;t to dobrodružstv&iacute;, využ&iacute;t sv&eacute; znalosti a dovednosti, abyste na&scaron;li cestu ven. Pr&aacute;vě takov&eacute; dobrodružstv&iacute; zažij&iacute; hlavn&iacute; postavy na&scaron;eho vypr&aacute;věn&iacute;! Přečtěte si poh&aacute;dkov&yacute; př&iacute;běh, kter&yacute; vych&aacute;z&iacute; z&nbsp;tematiky v&iacute;kendov&eacute;ho t&aacute;bora <u><a href=\"https://volnycasdeti.cz/udalost/ztraceni-v-chlebskem-udoli\"><span style=\"color:#FFFF00\">Ztraceni v&nbsp;chl&eacute;bsk&eacute;m &uacute;dol&iacute;</span></a></u>, kter&yacute; se uskutečnil od 1. do 3. června. Spolu s&nbsp;dětmi jsme oslavili nejen jejich Den, ale tak&eacute; jsme si během soutěž&iacute; volně inspirovan&yacute;ch zn&aacute;mou hrou Robinsonův ostrov užili spoustu kouzeln&eacute; z&aacute;bavy v&nbsp;kr&aacute;sn&eacute; př&iacute;rodě. A jak&eacute; v&yacute;zvy čekaj&iacute; na tři spoluž&aacute;ky, kteř&iacute; se ztratili v&nbsp;tajupln&eacute;m lese? Dozv&iacute;te se v&nbsp;na&scaron;em origin&aacute;ln&iacute;m ilustrovan&eacute;m e-booku.</p>\n',	44,	'/ebooks/5/ZtraceniObal3DNEW.png',	'/ebooks/5/Ztraceni-ebook.pdf',	1,	1,	5);

