DROP TABLE IF EXISTS `vcd_event`;
CREATE TABLE `vcd_event` (
  `id` int(8) unsigned NOT NULL AUTO_INCREMENT,
  `slug` varchar(255) COLLATE utf8_czech_ci NOT NULL,
  `type` tinyint(1) unsigned NOT NULL,
  `visible` tinyint(1) unsigned NOT NULL,
  `name` varchar(256) COLLATE utf8_czech_ci NOT NULL,
  `starts` datetime NOT NULL,
  `ends` datetime NOT NULL,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `applicable_until` datetime DEFAULT NULL,
  `max_participants` int(4) unsigned NOT NULL,
  `change_participants` int(4) NOT NULL DEFAULT '0',
  `max_reserves` int(4) unsigned NOT NULL,
  `age_min` int(2) unsigned NOT NULL,
  `age_max` int(2) unsigned NOT NULL,
  `age_cap` int(2) unsigned NOT NULL,
  `open_for_vip` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `price` int(6) unsigned NOT NULL,
  `price_vip` int(6) unsigned DEFAULT NULL,
  `deposit` int(6) unsigned NOT NULL,
  `sibling_discount` int(6) unsigned NOT NULL,
  `description` varchar(1024) COLLATE utf8_czech_ci DEFAULT NULL,
  `keywords` varchar(1024) COLLATE utf8_czech_ci DEFAULT NULL,
  `banner_small` varchar(256) COLLATE utf8_czech_ci DEFAULT NULL,
  `banner_large` varchar(256) COLLATE utf8_czech_ci DEFAULT NULL,
  `gallery_photo` varchar(256) COLLATE utf8_czech_ci DEFAULT NULL,
  `accepted_email` int(8) DEFAULT NULL,
  `is_archived` tinyint(1) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `slug` (`slug`),
  KEY `id` (`id`),
  KEY `type` (`type`),
  KEY `accepted_email` (`accepted_email`),
  KEY `backend_list_idx` (`is_archived`,`starts`),
  KEY `frontend_list_idx` (`is_archived`,`type`,`visible`,`starts`),
  KEY `frontend_archive_idx` (`is_archived`,`visible`,`starts`),
  CONSTRAINT `vcd_event_ibfk_1` FOREIGN KEY (`accepted_email`) REFERENCES `vcd_email` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_czech_ci;

